import React from "react";

function FormRegistration (props) {
    return (
        <div className='registration' id={props.name}>
            <div className="registration__container">
                <h2 className="registration__title">{props.title}</h2>
                <form action="#" name={props.name} className="registration__form" id={`registration__${props.name}`} 
                    onSubmit={props.handleSubmit} noValidate>
                    <fieldset className="registration__fieldset">
                        {props.children}
                        <button className={`registration__button-save ${!props.isFormValid && 'registration__button-save_inactive'}`} type="submit" id="reg-page__button-save-add">{props.textButton}</button>
                    </fieldset>
                </form>
                <button className="popup__button-exit" type="button"
                    onClick={props.onClose}></button>
            </div>
        </div>
    )
}

export default FormRegistration;
