import React from "react";
import PopupWithForm from "../PopupWithForm/PopupWithForm";

function AddPlacePopup (props) {

    const [title, setTitle] = React.useState('');
    const [link, setLink] = React.useState('');
    const [formValidity, setFormValidity] = React.useState({
        titleValid: false,
        linkValid: false
    })

    React.useEffect(() => {
        setTitle('');
        setLink('');
    }, [props.isOpen])

    React.useEffect (() => {
        const isTitleValid = title !== undefined && (title.length > 2 && title.length < 30);
        const isLinkValid = link !== undefined && link.length > 10;

        setFormValidity(() => ({
            titleValid: isTitleValid,
            linkValid: isLinkValid
        }))
    }, [title, link, setFormValidity])

    const handleTitleChange = (e) => {
        setTitle(e.target.value);
    }

    const handleLinkValue = (e) => {
        setLink(e.target.value);
    }

    function handleSubmit (e) {
        e.preventDefault();
        
        props.onAddPlace({
            name: title,
            link
        })
        
        props.onClose();
    }

    const titleValidity = formValidity.titleValid;
    const linkValidity = formValidity.linkValid;
    const formValid = (titleValidity === true && linkValidity === true) ? true : false;

    return (
        <PopupWithForm name='form-add-photo' title='Новое место' textButton={props.isLoading ? 'Сохранение...' : 'Создать'} 
        isOpen={props.isOpen} onClose={props.onClose} handleSubmit={handleSubmit} isFormValid={formValid}>
                        <label htmlFor="popup__input-title-pictire">
                            <input 
                            onChange={handleTitleChange} 
                            className={`popup__input popup__input_type_title $`} 
                            id="popup__input-title-pictire" 
                            minLength="2" maxLength="30" 
                            name="title" type="text" placeholder="Название" 
                            value={title || ''} 
                            required />
                            <span className="popup__input-error popup__input-title-pictire-error"></span>
                        </label>
                        <label htmlFor="popup__input-link">
                            <input 
                            onChange={handleLinkValue} 
                            className="popup__input popup__input_type_link" 
                            id="popup__input-link" 
                            name="link" type="url" placeholder="Ссылка на картинку" 
                            value={link || ''} 
                            required />
                            <span className="popup__input-error popup__input-link-error"></span>
                        </label>
        </PopupWithForm>
    )
} 

export default AddPlacePopup;
