import React from "react";
import PopupWithForm from "../PopupWithForm/PopupWithForm";

function Login () {

    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');
    const [formValidity, setFormValidity] = React.useState({
        emailValid: false,
        passwordValid: false
    })
    const emailValidity = formValidity.emailValid;
    const passwordValidity = formValidity.passwordValid;
    const formValid = emailValidity === true && passwordValidity === true;
    const [emailError, setEmailError] = React.useState('');
    const [passwordError, setPasswordError] = React.useState('');

    React.useEffect(() => {
        setEmail('');
        setPassword('');
    }, [])

    React.useEffect(() => {
        const httpRegex = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
        const isEmailValid = email !== undefined && httpRegex.test(email);

        const isPasswordValid = password !== undefined && password.length > 8;

        setEmailError(() => {
            if (email === '') {
                return 'Введите email'
            } else if (email !== '' && !isEmailValid) {
                return 'Введён некорректный email'
            }
        })

        setPasswordError(() => {
            if(password === '') {
                return 'Введите пароль'
            } else if (password !== '' && !isPasswordValid) {
                return 'Пароль должен содержать не менее 8 символов'
            }
        })

        setFormValidity({
            emailValid: isEmailValid,
            passwordValid: isPasswordValid
        })
    }, [email, password, setFormValidity])

    function handleLoginChange (e) {
        setLogin(e.target.value);
    }

    function handlePasswordChange (e) {
        setPassword(e.target.value);
    }

    return (
        <PopupWithForm>
            <label htmlFor="popup__input-login">
                <input 
                onChange={handleLoginChange} 
                className={`popup__input ${!emailValidity && 'popup__input_type_error'}`} 
                id="popup__input-login" 
                minLength="2" maxLength="30" 
                name="email" type="text" placeholder="email" 
                value={email || ''} 
                required />
                <span className={`popup__input-error popup__input-title-pictire-error ${!emailValidity && 'popup__input-error_active'}`}>{emailError}</span>
            </label>
            <label htmlFor="popup__input-password">
                <input
                onChange={handlePasswordChange} 
                className={`popup__input ${!passwordValidity && 'popup__input_type_error'}`} 
                id="popup__input-password" 
                name="password" type="password" placeholder="Пароль" 
                value={password || ''} 
                required />
                <span className={`popup__input-error popup__input-link-error ${!linkValidity && 'popup__input-error_active'}`}>{linkError}</span>
            </label>
        </PopupWithForm>
    )
}

export default Login;
