import React from "react";
import PopupWithForm from "../PopupWithForm/PopupWithForm";

function Login () {

    const [login, setLogin] = React.useState('');
    const [password, setPassword] = React.useState('');
    const [formValidity, setFormValidity] = React.useState({
        loginValid: '',
        passwordValid: ''
    })
    const loginValidity = formValidity.loginValid;
    const passwordValidity = formValidity.passwordValid;
    const formValid = loginValidity === true && passwordValidity === true;
    const [loginError, setLoginError] = React.useState('');
    const [passwordError, setPasswordError] = React.useState('');

    React.useEffect(() => {
        setLogin('');
        setPassword('');
    }, [])

    function handleLoginChange (e) {
        setLogin(e.target.value);
    }

    function handlePasswordChange (e) {
        setPassword(e.target.value);
    }

    return (
        <PopupWithForm>
            <label htmlFor="popup__input-login">
                <input 
                onChange={handleLoginChange} 
                className={`popup__input ${!titleValidity && 'popup__input_type_error'}`} 
                id="popup__input-login" 
                minLength="2" maxLength="30" 
                name="email" type="text" placeholder="email" 
                value={login || ''} 
                required />
                <span className={`popup__input-error popup__input-title-pictire-error ${!titleValidity && 'popup__input-error_active'}`}>{titleError}</span>
            </label>
            <label htmlFor="popup__input-password">
                <input
                onChange={handlePasswordChange} 
                className={`popup__input ${!linkValidity && 'popup__input_type_error'}`} 
                id="popup__input-password" 
                name="password" type="password" placeholder="Пароль" 
                value={password || ''} 
                required />
                <span className={`popup__input-error popup__input-link-error ${!linkValidity && 'popup__input-error_active'}`}>{linkError}</span>
            </label>
        </PopupWithForm>
    )
}

export default Login;
