import React from "react";
import FormRegistration from "../FormRegistration/FormRegistration";
import Header from "../Header/Header";

function Register (props) {
    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');
    const [formValidity, setFormValidity] = React.useState({
        emailValid: false,
        passwordValid: false
    })
    const emailValidity = formValidity.emailValid;
    const passwordValidity = formValidity.passwordValid;
    const formValid = emailValidity === true && passwordValidity === true;
    const [emailError, setEmailError] = React.useState('');
    const [passwordError, setPasswordError] = React.useState('');

    React.useEffect(() => {
        setEmail('');
        setPassword('');
    }, [])

    React.useEffect(() => {
        const httpRegex = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
        const isEmailValid = email !== undefined && httpRegex.test(email);

        const isPasswordValid = password !== undefined && password.length > 8;

        setEmailError(() => {
            if (email === '') {
                return 'Введите email'
            } else if (email !== '' && !isEmailValid) {
                return 'Введён некорректный email'
            }
        })

        setPasswordError(() => {
            if(password === '') {
                return 'Введите пароль'
            } else if (password !== '' && !isPasswordValid) {
                return 'Пароль должен содержать не менее 8 символов'
            }
        })

        setFormValidity({
            emailValid: isEmailValid,
            passwordValid: isPasswordValid
        })
    }, [email, password, setFormValidity])

    function handleLoginChange (e) {
        setEmail(e.target.value);
    }

    function handlePasswordChange (e) {
        setPassword(e.target.value);
    }

    return (
        <>
        
        </>
        
    )
}

export default Register;
